import React from "react";
import Container from "../Container/Container";

const Reviews = ({ className, children, style, reviews}) => {
    if (reviews.length <=0) return;

    return (<>
        <span className="py-1" style={{
            display: 'flex',
            alignItems: 'center',
            color: 'white',
            color: 'white',
            fontSize: '25px',
            fontWeight: '500',
            textShadow: '2px 2px rgba(80, 80, 100, 1)',
        }}>
            Lets hear the Critics
        </span>
        <Container>

            {
                reviews.map((review, indx) => {
                    return (<>
                    <div className="d-flex flex-column py-0" style={{
                        gap: '0px'
                    }}>
                        <span className="py-0" style={{
                                display: 'flex',
                                alignItems: 'center',
                                color: 'white',
                                fontSize: '20px',
                                fontWeight: '500',
                                textShadow: '2px 2px rgba(29, 29, 35, 1)',
                            }}>
                                {review.firstName || review.username}
                        </span>
                            {review.firstName &&
                        <span className="py-0" style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    color: 'rgba(225, 202, 240, 1)',
                                    fontSize: '12px',
                                    fontWeight: '500',
                                }}>
                                    {'@'+review.username}
                        </span>
                        }
                    </div>

                        <Container key={indx} className='my-1' style={{
                            backgroundColor: 'white'
                        }}>
                            {review.description}
                        </Container>
                    </>)

                })
            }
        </Container>
   </> )
}

export default Reviews;
