import { gql } from "@apollo/client";

export const GET_USER_LOGIN = gql`
    query loginUser(
        $username: String
        $password: String
    ) {
        login(
            username: $username,
            password: $password
        ) {
            id
            email
            username
        }
    } 
`;

export const GET_ALL_USERS = gql`
    query users {
        users {
            username,
            email,
            id,
            firstName,
            lastName,
        }
    }
`;

export const GET_MOVIE_REVIEWS = gql`
    query getMovieReviews(
        $movieId: Int
    ) {
        getMovieReviews(
            movieId: $movieId
        ) {
            id
            description
            authorId
            movieId
            rating
            created
        }
    } 
`
