const API_KEY = "3ad4aa0883a012ca4f7ceaed2b983305";
const DISCOVER_URL = "https://api.themoviedb.org/3/discover/movie?api_key=" + API_KEY;

const requests = {
    fetchPopular: DISCOVER_URL + "&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_watch_monetization_types=flatrate",
    fetchNewReleases: DISCOVER_URL + "&language=en-US&sort_by=release_date.desc&include_adult=false&include_video=false&page=1&primary_release_year="+new Date().getFullYear()+"&with_watch_monetization_types=flatrate",
    fetchHorror: DISCOVER_URL + "&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_genres=27&with_watch_monetization_types=flatrat",
    fetchAction: DISCOVER_URL + "&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_genres=28&with_watch_monetization_types=flatrat",
};

export default requests;
