import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import { useSelector, useDispatch } from 'react-redux';
import { setIsLoggedIn } from './../features/authentication/authSlice'

const LoginPage = () => {
    const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
    const dispatch = useDispatch();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const login = () => {
        Axios.post("http://localhost:3001/login", {
            username: username,
            password: password,
        }).then((response) => {
            if (response.data.message) {
                console.log(response.data.message);
            } else {
                console.log(response);
                dispatch(setIsLoggedIn(true));
            }
        });
    };

    return (
        <div className="container-fluid px-0" style={{
            background: 'rgba(29, 29, 35, 1)',
            paddingBottom: '5rem',
            paddingTop: '5rem',
        }}>
            <div className="container-fluid px-4">
                <h1 className="py-2 form-title" style={{
                    fontSize: '48px',
                    textShadow: '4px 4px rgba(80, 80, 100, 1)',
                }}>Login</h1>


                <form className="container-fluid py-3" style={{
                    backgroundColor: 'rgba(80, 80, 100, 1)',
                    borderTop: '4px solid rgba(225, 202, 240, 1)',
                    boxShadow: '10px 10px 20px black',
                    borderRadius: '3px',
                }}>
                <div className="mb-3">
                    <label htmlFor="exampleInputUsername1" className="form-label">Username *</label>
                    <input type="username" className="form-control" id="exampleInputUsername1" aria-describedby="usernameHelp"
                        onChange={(e) => {
                            setUsername(e.target.value);
                        }}
                    />
                </div>

                    <div className="mb-3">
                        <label htmlFor="exampleInputPassword1" className="form-label d-flex">Password *</label>
                        <input type="password" className="form-control" id="exampleInputPassword1"
                            onChange={(e) => {
                                setPassword(e.target.value);
                            }}
                        />
                    </div>

                <button type="button" className="btn-login"
                    onClick={login}
                >Login</button>
                </form>

                <label className="form-label ms-2 mt-2">Don't have an account? Become a critic <Link to="/register"
                    style={{
                        textDecoration: 'none',
                        color: "rgba(225, 202, 240, 1)",
                        borderBottom: '2px solid rgba(80, 80, 100, 1)'
                    }}
                >here.</Link></label>

            </div> 
        </div>
    )
}

export default LoginPage;
