import React, { useState } from 'react';
import Container from '../Container/Container';

const ReviewForm = ({ children, className, style }) => {
    const [review, setReview] = useState('');

    return (
        <form>
        <Container topBorder variant='shadow' className={className} style={{
            ...style
        }}>
            <div className="mb-3">
                    <label htmlFor="reviewInput" className="form-label">Leave a review</label>
                    <input type="text" className="form-control" id="reviewInput" aria-describedby="reviewHelp"
                        value={review}
                        onChange={(e) => {
                            setReview(e.target.value);
                        }}
                        style={{
                            height: '200px'
                        }}
                    />
                </div>
        </Container>
        </form>
    )
}

export default ReviewForm;
