import { gql } from "@apollo/client";

export const CREATE_USER = gql`
    mutation createUser(
            $username: String!
            $email: String!
            $password: String!
            $firstName: String
            $lastName: String
        ) {
            register(
                firstName: $firstName
                lastName: $lastName
                username: $username
                password: $password
                email: $email
            ) {
                id
                username
                lastName
                firstName
                created
                email
            }
        }

`;

export const CREATE_REVIEW = gql`
    mutation review(
            $authorId: String!
            $description: String!
            $movieId: Int!
            $rating: Float
        ) {
            createReview(
                authorId: $authorId
                description: $description
                authorId: $authorId
                rating: $rating
                movieId: $movieId
            ) {
                id
                created
            }
        }

`;
