import Reac, { useState, useEffect } from "react";

const IconRating = ({ className, children, style }) => {
    const [rating, setRating] = useState([]);
    const [isHovered, setIsHovered] = useState(false);

    useEffect(() => {
        const temp = [];
        [1,2,3,4,5].map((item, indx) => {
            temp.push({
                id: indx,
                isHovered: false,
            })
        });
        setRating(temp);
    }, []);

    const onHover = (id, value) => {
        var temp = [...rating];
        if (value === false) {
            temp = temp.map(item => {
                return {...item, isHovered: value};
            })
        } else {
            temp = temp.map((item) => {
                if (item.id <= id) {
                    return {...item, isHovered: value};
                } else {
                    return {...item, isHovered: !value};
                }

            })
        }
        setRating(temp);
    }


    return (
        <div className={''+className} style={{
            ...style
        }}>
            {rating.map((item, ind) => {
                return (
                    <i className="fa fa-star px-1" aria-hidden="true" style={{
                        color: !item.isHovered ? 'white' : 'red',
                        transition: 'all 0.1s'
                    }}
                    onMouseEnter={() => onHover(ind, true)}
                    onMouseLeave={() => onHover(ind, false)}
                    ></i>
                )
            })}
        </div>
    )
}

export default IconRating;
