import React, { useEffect, useState } from 'react';
import './App.css';
import MovieRow  from './Components/MovieRow/MovieRow';

const FEATURED_API = "https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=3ad4aa0883a012ca4f7ceaed2b983305&page=1";
const SEARCH_API = "https://api,themoviedb.org/3/search/movie?&api_key=3ad4aa0883a012ca4f7ceaed2b983305&query=";

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(FEATURED_API).then(res => res.json())
    .then(data => {
      console.log(data);
      setMovies(data);
    });
  }, [])

  return (
    <div className="App">
        <MovieRow movies={movies}/>
    </div>
  );
}

export default App;
