import axios from 'axios';
import { createSlice, createAsyncThunk, current } from '@reduxjs/toolkit';

export const getReviews = createAsyncThunk('movie/getReviews', async ({ movieId }) => {
    return await axios.get("http://localhost:3001/getReviews", {
              params: {
                movieId: movieId,
              }
          }).then((response) => {
              if (response.data.message) {
                  console.log(response.data.message);
                  return null;
              } else {
                  return response.data;
              }
          });
});


export const addLike = createAsyncThunk('review/addLike', async ({ reviewId, userId }) => {
    return await axios.post("http://localhost:3001/addLike", {
        reviewId: reviewId,
        likerId: userId
    }).then((response, err) => {
        if (response.data.success === false) {
            console.log(response.data.message);
            return null;
        } else {
            return response.data;
        }
    })
});

export const removeLike = createAsyncThunk('review/removeLike', async ({ reviewId, userId }) => {
    return await axios.post("http://localhost:3001/removeLike", {
        reviewId: reviewId,
        likerId: userId
    }).then((response, err) => {
        if (response.data.success === false) {
            console.log(response.data.message);
            return null;
        } else {
            return response.data;
        }
    })
});

export const movieSlice = createSlice({
    name: 'movie',
    initialState: {
        isLoading: true,
        movieInfo: {

        },
        reviews: []
    },
    reducers: {
        addReview: (state, action) => {
            state.reviews.push(action.payload);
        },
        incrimentLike: (state, action) => {
            var temp = current(state).reviews.map((review) => {
                if (review.id === action.payload) {
                    return {
                        ...review,
                        likes: review.likes + 1
                    }
                }
                return review;
            });
            state.reviews = temp;
        },
        decrimentLike: (state, action) => {
            var temp = current(state).map((review) => {
                if (review.id === action.payload) {
                    return {
                        ...review,
                        likes: review.likes - 1
                    }
                }
                return review;
            });
            state.reviews = temp;
        },
    },
    extraReducers: {
        [getReviews.fulfilled]: (state, action) => {
            if (action.payload) {
                state.reviews = action.payload;
                state.isLoading = false;
            } 
        },
        [addLike.fulfilled]: (state, action) => {
            if (action.payload) {
                state.reviews.map((review, indx) => {
                    if (review.id === action.payload.reviewId) {
                        state.reviews[indx].likes += 1;
                    }
                })
            }
        }

    }
});

export const { addReview, incrimentLike, decrimentLike } = movieSlice.actions;




export default movieSlice.reducer;
