import React from "react";
import Container from "../Container/Container";
import IconRating from "../IconRating/IconRating";

const Reviews = ({ className, children, style, reviews}) => {
    if (reviews.length <=0) return;

    const createdDate = (mySqlDate) => {
        if (mySqlDate.length <= 0) return;

        var tempArr = mySqlDate.slice(0,10).split("-");

        return parseInt(tempArr[2])+"/"+parseInt(tempArr[1])+"/"+parseInt(tempArr[0]);
    }

    const createdTime = (mySqlDate) => {
        console.log(mySqlDate)
        if (mySqlDate.length <= 0) return;
        var tempTimeArr = mySqlDate.slice(11,mySqlDate.length-5).split(":");
        return parseInt(tempTimeArr[0])+":"+parseInt(tempTimeArr[1])+":"+parseInt(tempTimeArr[2]);
    }

    return (<>
        <span className="py-0" style={{
            display: 'flex',
            alignItems: 'center',
            color: 'white',
            color: 'white',
            fontSize: '25px',
            fontWeight: '500',
            textShadow: '2px 2px rgba(80, 80, 100, 1)',
        }}>
            Lets hear the Critics
        </span>
        <Container style={{
            padding: '0rem',
            backgroundColor: 'none'
        }}>

            {
                reviews.map((review, indx) => {
                    var stars = [];
                    for (let i = 0; i < review.entertainment; i++) {
                        stars.push(i);
                    }

                    return (<div className="py-2 pt-0">

                        <Container key={indx} className='my-1' style={{
                            padding: '1rem'
                        }}>
                            <div className='d-flex'>
                                <button type="button" className="btn-login draw meet my-1 me-auto">{review.firstName || review.username}</button>
                                <div className="d-flex">
                                    {
                                        stars.map((star) => {
                                            return (
                                                <i className="fa fa-star px-1 d-flex align-items-center" aria-hidden="true" style={{
                                                    color:  'rgba(225, 202, 240, 1)',
                                                    textShadow: '2px 2px black',
                                                    transition: 'all 0.1s',
                                                    fontSize: '20px'
                                                }}></i>
                                            )
                                        })
                                    }
                                </div>
                            </div>

                            <Container style={{
                                padding: '0rem',
                                color: 'white'
                            }}>
                                {review.description}
                            </Container>

                            <div className="d-flex align-items-center">
                                <div className="d-flex flex-column">
                                    <span >{review.likes || 0 + ' likes'}</span>
                                    <button className="btn-large draw meet p-1"><i class="fa fa-thumbs-up-o" aria-hidden="true" style={{
                                        fontSize: '25px'
                                    }}></i></button>
                                </div>

                                <div className="d-flex flex-column ms-auto">
                                    <span className="ms-auto" style={{
                                        fontSize: '13px'
                                    }}>{createdTime(review.created)}</span>
                                    <span className="ms-auto">{createdDate(review.created)}</span>
                                </div>
                            </div>


                        </Container>
                    </div>)

                })
            }
        </Container>
   </> )
}

export default Reviews;
