import axios from 'axios';
import { createSlice, createAsyncThunk, current } from '@reduxjs/toolkit';

export const getLogin = createAsyncThunk('user/getLogin', async ({ username, password}) => {
    return await axios.post("http://localhost:3001/login", {
              username: username,
              password: password,
          }).then((response) => {
            console.log(response);
              return response.data;
          });
  })

export const getRegister = createAsyncThunk('user/getRegister', async ({ username, firstName, lastName, password, email}) => {
    return await axios.post('http://localhost:3001/register', {
        username: username,
        password: password,
        firstName: firstName,
        lastName: lastName,
        email: email,
        created: new Date().toISOString().slice(0, 19).replace('T', ' ')
    }).then((response, err) => {
        if (response.data.success === false) {
            console.log(response.data.message);
            return null;
        } else {
            console.log(response.data.user);
            return response.data.user;
        }
    });
});

export const getUserLikes = createAsyncThunk('user/getUserLikes', async ({ userId }) => {
    return await axios.get("http://localhost:3001/getUserLikes", { params: {
        userId: userId
    } }).then((response) => {
              if (response.data.message) {
                  console.log(response.data.message);
                  return null;
              } else {
                  return response.data;
              }
          });
  })

export const authSlice = createSlice({
    name: 'user',
    initialState: {
        isLoggedIn: false,
        user: {
            id: -1,
            firstName: '',
            lastName: '',
            username: '',
            password: '',
            email: '',
            created: '',
        },
        userLikes: []
    },
    reducers: {
        setIsLoggedIn: (state, action) => {
            state.isLoggedIn = action.payload;
        },
        setFirstName: (state, action) => {
            state.user.firstName = action.payload;
        },
        setLastName: (state, action) => {
            state.user.lastName = action.payload;
        },
        setUsername: (state, action) => {
            state.user.username = action.payload;
        },
        setEmail: (state, action) => {
            state.user.email = action.payload;
        },
        setCreated: (state, action) => {
            state.user.created = action.payload;
        },
        setUser: (state, action) => {
            if (action.payload === null) {
               state.isLoggedIn = false;
            } else {
                state.isLoggedIn = true;
                state.user = action.payload;
            }
        },
        addUserLike: (state, action) => {
            state.userLikes.push(action.payload);
        },
        removeUserLike: (state, action) => {
            var delIndex = -1;
            const tempLikes = current(state).userLikes.map((like, indx) => {
                if (like.likerId === action.payload.likerId && like.reviewId === action.payload.reviewId) {
                    delIndex = indx;
                }
                return like;
            });
            if (delIndex > -1) {
                tempLikes.splice(delIndex, 1);
            }
            state.userLikes = tempLikes;
        }
    },
    extraReducers: {
        [getLogin.fulfilled]: (state, action) => {
            if (action.payload) {
                state.user = action.payload;
                state.isLoggedIn = true;
            }
        },
        [getRegister.fulfilled]: (state, action) => {
            if (action.payload) {
                state.user = action.payload;
                state.isLoggedIn = true;
            }
        },
        [getUserLikes.fulfilled]: (state, action) => {
            if (action.payload) {
                state.userLikes = action.payload.result;
            }
        }
    }
});

export const { setIsLoggedIn, setUser, setFirstName, setLastName, setPassword, setUsername, setEmail, addUserLike, removeUserLike } = authSlice.actions;




export default authSlice.reducer;
