import React, { useEffect, useState } from 'react';
import './MovieRow.css';

const IMG_API = "https://image.tmdb.org/t/p/w1280";

const MovieRow = ({category, fetchUrl }) => {

    const [movies, setMovies] = useState([]);

    useEffect(() => {
        fetch(fetchUrl).then(res => res.json())
        .then(data => {
          console.log(data.results);
          setMovies(data.results);
        });
      }, [fetchUrl])

    return (
        <div className="category-row m-1 ms-1">
            <h2>{category}</h2>
            <div className="poster-row py-1">

            {movies.length > 0 &&
                movies.map(movie => {
                    if (movie.poster_path){
                        return (<>
                            <img className="poster-img" src={IMG_API + movie.poster_path} alt={movie.title} />
                        </>)
                    }

                })
            }</div>
        </div> 
    )
}

export default MovieRow;
