import axios from 'axios';
import { createSlice, createAsyncThunk, current } from '@reduxjs/toolkit';

export const addLike = createAsyncThunk('review/addLike', async ({ reviewId, userId, likeDate }) => {
    return await axios.post("http://localhost:3001/addLike", {
        reviewId: reviewId,
        likerId: userId,
        likeDate: likeDate,
    }).then((response, err) => {
        if (response.data.success === false) {
            console.log(response.data.message);
            return null;
        } else {
            return response.data;
        }
    })
});

export const removeLike = createAsyncThunk('review/removeLike', async ({ reviewId, userId }) => {
    return await axios.post("http://localhost:3001/removeLike", {
        reviewId: reviewId,
        likerId: userId
    }).then((response, err) => {
        if (response.data.success === false) {
            console.log(response.data.message);
            return null;
        } else {
            return response.data;
        }
    })
});

export const movieSlice = createSlice({
    name: 'movie',
    initialState: {
        isLoading: true,
        movieInfo: {

        },
        reviews: []
    },
    reducers: {
        addReview: (state, action) => {
            var temp = current(state).reviews;
            temp = [...temp, action.payload];
            state.reviews = temp;
        },
        setReviews: (state, action) => {
            state.reviews = action.payload;
        },
        incrimentLike: (state, action) => {
            var reviewId = action.payload.reviewId;
            var like = action.payload.like;

            var temp = current(state)
            var index = temp.reviews.findIndex(review => review.id === reviewId)
            temp = temp.reviews[index].likes;
            temp = [...temp,{...like}];
            state.reviews[index].likes = temp;
        },
        decrimentLike: (state, action) => {
            var reviewId = action.payload.reviewId;
            var likeId = action.payload.likeId;

            var temp = current(state).reviews;
            var reviewIndex = temp.findIndex(review => review.id === reviewId);

            temp = current(state).reviews[reviewIndex].likes;
            console.log(temp)
            var tempLikes = [];
            for (var i = 0; i < temp.length; i++) {
                if (temp[i].id !== likeId) {
                    tempLikes.push(temp[i]);
                }
            }
            state.reviews[reviewIndex].likes = tempLikes;
        },
    },
    extraReducers: {
        [addLike.fulfilled]: (state, action) => {
            if (action.payload) {
                state.reviews.forEach((review, indx) => {
                    if (review.id === action.payload.reviewId) {
                        state.reviews[indx].likes += 1;
                    }
                })
            }
        }

    }
});

export const { addReview, setReviews, incrimentLike, decrimentLike } = movieSlice.actions;




export default movieSlice.reducer;
