import React from "react";
import Container from "../Container/Container";

const Reviews = ({ className, children, style, reviews}) => {
    if (reviews.length <=0) return;

    return (<>
        <span className="py-0" style={{
            display: 'flex',
            alignItems: 'center',
            color: 'white',
            color: 'white',
            fontSize: '25px',
            fontWeight: '500',
            textShadow: '2px 2px rgba(80, 80, 100, 1)',
        }}>
            Lets hear the Critics
        </span>
        <Container style={{
            padding: '0rem',
            backgroundColor: 'none'
        }}>

            {
                reviews.map((review, indx) => {
                    return (<div className="py-2 pt-0">

                        <Container key={indx} className='my-1' style={{
                            padding: '1rem'
                        }}>
                            <div className='d-flex'>
                                <button type="button" className="btn-login draw meet my-1">{review.firstName || review.username}</button>
                                <IconRating score={review.entertainment}/>
                            </div>

                            <Container style={{
                                padding: '0rem',
                                color: 'white'
                            }}>
                                {review.description}
                            </Container>
                        </Container>
                    </div>)

                })
            }
        </Container>
   </> )
}

export default Reviews;
