import React from 'react';
import './Rating.css';

const Rating = ({ style, children, score }) => {

    const ratingColor = (score) => {
        if (score <= 0) {
            return "white";
        } else if (score <= 5) {
            return "red";
        } else if (score <= 8) {
            return "orange";
        } else {
            return "lightgreen";
        }
    }

    return (
        <div className="rating-container" style={{
            color: ratingColor(score)
        }}>
            {score === 0 ? "NA" : score}
        </div>
    )
}

export default Rating;
