import React, { useEffect } from "react";
import { addLike, removeLike, incrimentLike, decrimentLike } from "../../features/movie/movieSlice";
import Container from "../Container/Container";

 import { 
    getUserLikes,
    addUserLike,
    removeUserLike
 } from './../../features/authentication/authSlice';
 import { useDispatch, useSelector } from 'react-redux';

import './Reviews.css';

const Reviews = ({ className, children, style, reviews}) => {
    const user = useSelector((state) => state.auth.user);
    const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
    const userLikes = useSelector((state) => state.auth.userLikes);
    const dispatch = useDispatch();

    useEffect(() => {
        if (isLoggedIn) {
            dispatch(getUserLikes({ userId: user.id })).then((res, err) => {
            })
        }

    }, [isLoggedIn])

    const onAddLike = (review) => {
        dispatch(addLike({
            reviewId: review.id, 
            userId: user.id,
            likeDate: new Date().toISOString().slice(0, 19).replace('T', ' '),
        })).then((res, err) => {
            if (res) {
                if (res.payload.success === true) {
                    dispatch(addUserLike(res.payload.result[0]));
                    dispatch(incrimentLike(review.id));
                }
            } else {
            }
        });
    }

    const onUnLike = (review) => {
        dispatch(removeLike({reviewId: review.id, userId: user.id})).then((res, err) => {
            if (res) {
                dispatch(removeUserLike(res.payload.result));
                dispatch(decrimentLike(review.id));
            }

        });
    }

    if (reviews.length <=0) return;

    const createdDate = (mySqlDate) => {
        if (mySqlDate.length <= 0) return;

        var tempArr = mySqlDate.slice(0,10).split("-");

        return parseInt(tempArr[2])+"/"+parseInt(tempArr[1])+"/"+parseInt(tempArr[0]);
    }

    const createdTime = (mySqlDate) => {
        console.log(mySqlDate)
        if (mySqlDate.length <= 0) return;
        var tempTimeArr = mySqlDate.slice(11,mySqlDate.length-5).split(":");
        let minutes = '';
        if (parseInt(tempTimeArr[1]) < 10) {
            minutes = '0'+parseInt(tempTimeArr[1]);
        } else {
            minutes = ''+parseInt(tempTimeArr[1])
        }
        return parseInt(tempTimeArr[0])+":"+minutes;
    }

    const renderLikeButtons = (review) => {
        if (userLikes.length > 0) {
            const userLikedReviewsIds = userLikes.map(like => {
                return like.reviewId;
            })
            if (userLikedReviewsIds.includes(review.id)) {
                return (<>
                    <button className="btn-small pe-2 like-button"
                    onClick={() => onUnLike(review)}
                    ><i className="fa fa-thumbs-up" aria-hidden="true" style={{
                        fontSize: '20px',
                        color: 'rgba(225, 202, 240, 1)'
                    }}
                    ></i></button>
                </>)
            }
        } else if (!isLoggedIn) {
            return (<>
                <div className="btn-small pe-2 like-button"
                ><i className="fa fa-thumbs-up" aria-hidden="true" style={{
                    fontSize: '20px',
                    color: 'rgba(29, 29, 35, 1)'
                }}
                ></i></div>
            </>)
        }

        return (<>
            <button className="btn-small draw meet pe-2 like-button"
                onClick={() => {onAddLike(review)}}
            ><i className="fa fa-thumbs-o-up" aria-hidden="true" style={{
                fontSize: '20px',
            }}
            ></i></button>
        </>)
    }

    const renderReview = (review, indx) => {
                    var stars = [];
                    var color = () => {
                        if (review.entertainment >= 4) {
                            return 'green';
                        } else if (review.entertainment >= 2) {
                            return 'orange';
                        } else {
                            return 'red';
                        }
                    }
                    for (let i = 0; i < review.entertainment; i++) {
                        stars.push(i);
                    }

                    return (<div className="py-2 pt-0">

                        <Container variant="shadow" key={indx} className='my-1' style={{
                            padding: '1rem'
                        }}>
                            <div className='d-flex'>
                                <div className="me-auto my-1">
                                    <button type="button" className="btn-login draw meet me-2">{review.firstName || review.username}</button>
                                    <span style={{
                                        fontSize: '12px'
                                    }}>{review.firstName && '@'+review.username}</span>
                                </div>

                                <div className="d-flex">
                                    {
                                        stars.map((star) => {
                                            return (
                                                <i className="fa fa-star px-1 d-flex align-items-center" aria-hidden="true" style={{
                                                    color:  color(),
                                                    textShadow: '2px 2px black',
                                                    transition: 'all 0.1s',
                                                    fontSize: '20px'
                                                }}></i>
                                            )
                                        })
                                    }
                                </div>
                            </div>

                            <Container style={{
                                padding: '0rem',
                                color: 'white'
                            }}>
                                {review.description}
                            </Container>

                            <div className="d-flex gap-2">
                                <div className="d-flex align-items-center gap-1" style={{
                                    color: 'white',
                                    fontSize: '12px',
                                }}>
                                    <span>{review.likes}</span>
                                    <i class="fa fa-thumbs-up" aria-hidden="true"></i>
                                </div>
                            </div>



                            <hr style={{
                                marginTop: '0.5rem',
                                marginBottom: '0.5rem'
                            }}/>

                            <div className="d-flex align-items-center">
                                {renderLikeButtons(review)}

                                <div className="d-flex flex-column ms-auto" style={{
                                        fontSize: '12px'
                                    }}>
                                    <span className="ms-auto">{createdTime(review.created)}</span>
                                    <span className="ms-auto">{createdDate(review.created)}</span>
                                </div>
                            </div>


                        </Container>
                    </div>)
    }


    return (<div className={className}>
        <span className="py-0" style={{
            display: 'flex',
            alignItems: 'center',
            color: 'white',
            color: 'white',
            fontSize: '25px',
            fontWeight: '500',
            textShadow: '2px 2px rgba(80, 80, 100, 1)',
        }}>
            Lets hear from the Critics
        </span>
        <Container style={{
            padding: '0rem',
            backgroundColor: 'none'
        }}>

            {
                reviews.map((review, indx) => {
                    return renderReview(review, indx); 
                })
            }
        </Container>
   </div> )

}

export default Reviews;
