import React, { useEffect, useState } from 'react';
import './App.css';
import MovieRow  from './Components/MovieRow/MovieRow';
import requests from './Constants/Requests';

const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=3ad4aa0883a012ca4f7ceaed2b983305&query=";

function App() {

  return (
    <div className="App">
        <div>
          <MovieRow backdrop fetchUrl={requests.fetchPopular} category="Trending"/>
          <div>

          {
            requests.map((req, id) => {
              return (
                <div className="bg-dark">
                  <MovieRow fetchUrl={req.url} category={req.name}/>
                </div>
              )
            })
          }

          </div>


        </div>

    </div>
  );
}

export default App;
