import React from 'react';


const IMG_API = "https://image.tmdb.org/t/p/w1280";

const MovieCard = ({ title, poster_path, overview }) => {
    return (
        <div>
            <img style={{
                width: '100%',
                objectFit: 'contain',
                maxHeight: '100px',
                marginRight: '10px',
                transition: 'transform 450ms',  
            }}
            src={IMG_API + poster_path} alt={title}/>
        </div>
    )
}

export default MovieCard;
