import React from "react";
import { addLike } from "../../features/movie/movieSlice";
import Container from "../Container/Container";
import { 
    addDislike,
 } from './../../features/movie/movieSlice';
 import { useDispatch, useSelector } from 'react-redux';

import './Reviews.css';

const onAddLike = () => {
    dispatch(addLike);
}

const onAddDislike = () => {
    dispatch(addDislike);
}

const Reviews = ({ className, children, style, reviews}) => {
    const dispatch = useDispatch();

    if (reviews.length <=0) return;

    const createdDate = (mySqlDate) => {
        console.log(mySqlDate)
        if (mySqlDate.length <= 0) return;

        var tempArr = mySqlDate.slice(0,10).split("-");

        return parseInt(tempArr[2])+"/"+parseInt(tempArr[1])+"/"+parseInt(tempArr[0]);
    }

    const createdTime = (mySqlDate) => {
        if (mySqlDate.length <= 0) return;
        var tempTimeArr = mySqlDate.slice(11,mySqlDate.length-5).split(":");
        let minutes = '';
        if (parseInt(tempTimeArr[1]) < 10) {
            minutes = '0'+parseInt(tempTimeArr[1]);
        } else {
            minutes = ''+parseInt(tempTimeArr[1])
        }
        return parseInt(tempTimeArr[0])+":"+minutes;
    }

    return (<div className={className}>
        <span className="py-0" style={{
            display: 'flex',
            alignItems: 'center',
            color: 'white',
            color: 'white',
            fontSize: '25px',
            fontWeight: '500',
            textShadow: '2px 2px rgba(80, 80, 100, 1)',
        }}>
            Lets hear from the Critics
        </span>
        <Container style={{
            padding: '0rem',
            backgroundColor: 'none'
        }}>

            {
                reviews.map((review, indx) => {
                    var stars = [];
                    var color = () => {
                        if (review.entertainment >= 4) {
                            return 'green';
                        } else if (review.entertainment >= 2) {
                            return 'orange';
                        } else {
                            return 'red';
                        }
                    }
                    for (let i = 0; i < review.entertainment; i++) {
                        stars.push(i);
                    }

                    return (<div className="py-2 pt-0">

                        <Container variant="shadow" key={indx} className='my-1' style={{
                            padding: '1rem'
                        }}>
                            <div className='d-flex'>
                                <button type="button" className="btn-login draw meet my-1 me-auto">{review.firstName || review.username}</button>
                                <div className="d-flex">
                                    {
                                        stars.map((star) => {
                                            return (
                                                <i className="fa fa-star px-1 d-flex align-items-center" aria-hidden="true" style={{
                                                    color:  color(),
                                                    textShadow: '2px 2px black',
                                                    transition: 'all 0.1s',
                                                    fontSize: '20px'
                                                }}></i>
                                            )
                                        })
                                    }
                                </div>
                            </div>

                            <Container style={{
                                padding: '0rem',
                                color: 'white'
                            }}>
                                {review.description}
                            </Container>

                            <div className="d-flex gap-2">
                                <div className="d-flex align-items-center gap-1" style={{
                                    color: 'white',
                                    fontSize: '12px',
                                }}>
                                    <span>{review.likes}</span>
                                    <i class="fa fa-thumbs-up" aria-hidden="true"></i>
                                </div>

                                <div className="d-flex align-items-center gap-1" style={{
                                    color: 'white',
                                    fontSize: '12px',
                                }}>
                                    <span>{review.dislikes}</span>
                                    <i class="fa fa-thumbs-down" aria-hidden="true"></i>
                                </div>
                            </div>



                            <hr style={{
                                marginTop: '0.5rem',
                                marginBottom: '0.5rem'
                            }}/>

                            <div className="d-flex align-items-center">
                                <button className="btn-small draw meet pe-2 like-button"><i class="fa fa-thumbs-o-up" aria-hidden="true" style={{
                                    fontSize: '20px',
                                }}
                                onClick={() => onAddLike()}

                                ></i></button>
                                <button className="btn-small draw meet like-button"><i class="fa fa-thumbs-o-down" aria-hidden="true" style={{
                                    fontSize: '20px',
                                }}
                                onClick={() => onAddDislike()}
                                ></i></button>

                                <div className="d-flex flex-column ms-auto" style={{
                                        fontSize: '12px'
                                    }}>
                                    <span className="ms-auto">{createdTime(review.created)}</span>
                                    <span className="ms-auto">{createdDate(review.created)}</span>
                                </div>
                            </div>


                        </Container>
                    </div>)

                })
            }
        </Container>
   </div> )
}

export default Reviews;
