import React from 'react';
import './MovieRow.css';

const IMG_API = "https://image.tmdb.org/t/p/w1280";

const MovieRow = ({ movies }) => {

    return (
        <div className="movie-row"
        >

        {movies.length > 0 &&
            movies.map(movie => {
                return (
                    <div className="poster-row" key={movie.id}>
                        <img className="poster-img" src={IMG_API + movie.poster_path} alt={movie.title} />
                    </div>
                )
            })
        }</div>

    )
}

export default MovieRow;
