import React, { useState } from 'react';
import Container from '../Container/Container';
import { useSelector, useDispatch } from 'react-redux';
import { 
    getReviews,
 } from '../../features/movie/movieSlice';
import IconRating from '../IconRating/IconRating';

import { useMutation } from '@apollo/client';
import { CREATE_REVIEW } from '../../Graphql/Mutations';

const ReviewForm = ({ children, className, style, movieId }) => {
    const [createReview, { error, isloading }] = useMutation(CREATE_REVIEW);

    const dispatch = useDispatch();
    const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
    const user = useSelector((state) => state.auth.user);

    const [entertainment, setEntertainment] = useState(0.0);
    const [quality, setQuality] = useState(0);
    const [review, setReview] = useState('');

    const onSubmitReview = async () => {
        if (review.length === 0 && entertainment === 0) return;

        const response = await createReview({
               variables: {
                description: review,
                authorId: parsInt(user.id),
                movieId: movieId,
                rating: entertainment,
                created: new Date().toISOString().slice(0, 19).replace('T', ' '),
               }
           }).catch(error => {
               //showError(error.message);
               return { successful: false, message: error.message }   
           });
           console.log({
            description: review,
            authorId: user.id,
            movieId: movieId,
            rating: entertainment,
            created: new Date().toISOString().slice(0, 19).replace('T', ' '),
           })
           if (response.hasOwnProperty('data')) {
               //dispatch(setUser(response.data.createUser));
               //history.push("/");
           }
        /*
        axios.post("http://localhost:3001/addReview", newReview).then(response => {
            if (response.success === false) {
                console.log(response.message);
            } else {
                dispatch(getReviews({ movieId: movieId })).then((response) => {
                    //console.log(response.payload);
                });
                setReview('');
                setEntertainment(0);
                setQuality(0);
            }
        })
        */
    }

    const ratingGrade = (ratingData) => {
        var count = 0;
        ratingData.map(item => {
            if (item.isHovered === true) {
                count += 1;
            }
        });
        setEntertainment(count);
        return count;
    }

    if (!isLoggedIn) {
        return (
            <Container topBorder variant='shadow' className={className} style={{
                ...style
            }}>
                <label htmlFor="reviewInput" className="form-label">Sign in to leave a review!</label>
            </Container>
        )
    }

    return (
        <form>
        <Container topBorder variant='shadow' className={className} style={{
            ...style
        }}>
            <div className="my-3">
                    <label htmlFor="reviewInput" className="form-label py-1">Leave a review!</label>
                    <textarea type="text" className="form-control" id="reviewInput" aria-describedby="reviewHelp"
                        value={review}
                        placeholder="What was your favorite part?"
                        onChange={(e) => {
                            setReview(e.target.value);
                        }}
                        style={{
                            height: '200px',
                            display: 'inline',
                        }}
                    />

                <div className="d-flex my-2">
                    <button type="button" className="btn-login draw meet me-2"
                        onClick={onSubmitReview}
                    >Submit</button>
                    <IconRating handleChange={ratingGrade}></IconRating>
                </div>
            </div>
        </Container>
        </form>
    )
}

export default ReviewForm;
