import React, { useEffect, useState } from 'react';
import './App.css';
import MovieRow  from './Components/MovieRow/MovieRow';
import requests from './Constants/Requests';

const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=3ad4aa0883a012ca4f7ceaed2b983305&query=";

function App() {

  return (
    <div className="App">
        <div>
          <div style={{
            position: 'absolute',
            background: '-webkit-linear-gradient(rgba(13, 13, 12, 0.2), rgba(13, 13, 12, 1))',
            background: '-moz-linear-gradient(rgba(13, 13, 12, 0.2), rgba(13, 13, 12, 1))',
            background: '-o-linear-gradient(rgba(13, 13, 12, 0.2), rgba(13, 13, 12, 1))',
            background: 'linear-gradient(rgba(13, 13, 12, 0.2), rgba(13, 13, 12, 1))',
          }}>
          </div>
          <MovieRow backdrop fetchUrl={requests.fetchPopular} category="Trending"/>
          <div style={{
            marginTop: '77vh',
          }}>
          <MovieRow fetchUrl={requests.fetchNewReleases} category="New Releases"/>
          <MovieRow fetchUrl={requests.fetchHorror} category="Horror"/>
          <MovieRow fetchUrl={requests.fetchAction} category="Action"/>
          </div>


        </div>

    </div>
  );
}

export default App;
