import React, { useEffect, useState } from 'react';
import './MovieRow.css';

const IMG_API = "https://image.tmdb.org/t/p/w1280";

const MovieRow = ({category, fetchUrl, backdrop, style }) => {

    const [movies, setMovies] = useState([]);

    useEffect(() => {
        fetch(fetchUrl).then(res => res.json())
        .then(data => {
          console.log(data.results);
          setMovies(data.results);
        });
      }, [fetchUrl])

      const path = backdrop ? "backdrop_path" : "poster_path";

    if (path === "poster_path") {
        return (
            <div className="category-row ms-4 mt-4">
                <h2 className="m-2" style={{
                    position: "relative",
                    textShadow: '2px 2px rgba(80, 80, 100, 1)'
                }}>{category}</h2>
                <div className="poster-row">

                {movies.length > 0 &&
                    movies.map((movie, id) => {
                        if (movie[path]){
                            return (<>
                                <img key={id} className="poster-img m-2" src={IMG_API + movie[path]} alt={movie.title} />
                            </>)
                        }

                    })
                }</div>
            </div> 
        )
    }


    return (
        <div className="category-row-lg">

                <div id="carouselExampleControls" class="carousel slide" data-bs-ride="carousel">
                <div class="carousel-inner">

                {movies.length > 0 &&
                    movies.map((movie, id) => {
                        if (movie[path]){
                            return (<div className={"carousel-item" + (id === 1) && " active"} ><div className="d-block w-100">
                                <h1 className="movie-title">{movie.title}</h1>
                                <img key={id} className="poster-img-lg" src={IMG_API + movie[path]} alt={movie.title} />
                            </div></div>)
                        }

                    })
                }

                </div>
                <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
                    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span class="visually-hidden">Previous</span>
                </button>
                <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                    <span class="carousel-control-next-icon" aria-hidden="true"></span>
                    <span class="visually-hidden">Next</span>
                </button>
                </div>                    

            </div> 
    )

}

export default MovieRow;
