import React from 'react';

const Rating = ({ style, children, score, variant }) => {

    const containerStyleSmall = {
        borderRadius: '50%',
        width: '36px',
        height: '36px',
        padding: '8px',

        background: 'rgba(80, 80, 100, 1)',
        fontSize: '14px',
        textSlign: 'center',

        textShadow: '1px 1px rgba(29, 29, 35, 1)',
        boxShadow: '-2px -2px rgba(225, 202, 240, 1)',
    }

    const ratingColor = (score) => {
        if (score <= 0) {
            return "white";
        } else if (score <= 5) {
            return "red";
        } else if (score <= 8) {
            return "orange";
        } else {
            return "lightgreen";
        }
    }

    return (
        <div className="rating-container" style={{
            color: ratingColor(score),
            ...containerStyle,
            ...style,
        }}>
            {score === 0 ? "NA" : score}
        </div>
    )
}

export default Rating;
