import React, { useEffect, useState } from 'react';
import './MovieRow.css';

const IMG_API = "https://image.tmdb.org/t/p/w1280";

const MovieRow = ({category, fetchUrl, backdrop, style }) => {

    const [movies, setMovies] = useState([]);

    useEffect(() => {
        fetch(fetchUrl).then(res => res.json())
        .then(data => {
          console.log(data.results);
          setMovies(data.results);
        });
      }, [fetchUrl])

      const path = backdrop ? "backdrop_path" : "poster_path";

    if (path === "poster_path") {
        return (
            <div className="category-row ms-4 mt-4">
                <h2 className="m-2" style={{
                    position: "relative"
                }}>{category}</h2>
                <div className="poster-row">

                {movies.length > 0 &&
                    movies.map((movie, id) => {
                        if (movie[path]){
                            return (<>
                                <img key={id} className="poster-img m-2" src={IMG_API + movie[path]} alt={movie.title} />
                            </>)
                        }

                    })
                }</div>
            </div> 
        )
    }


    return (
        <div className="category-row-lg">

                <div className="poster-row-lg">

                {movies.length > 0 &&
                    movies.map((movie, id) => {
                        if (movie[path]){
                            return (<div className="d-flex">
                            <div className="movie-title">
                                <h1>{movie.title}</h1>
                            </div>

                                <img key={id} className="poster-img-lg w-100" src={IMG_API + movie[path]} alt={movie.title} />
                            </div>)
                        }

                    })
                }</div>
            </div> 
    )

}

export default MovieRow;
