import React, { useState } from 'react';
import Container from '../Container/Container';
import { useSelector } from 'react-redux';

const ReviewForm = ({ children, className, style }) => {
    const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
    const [review, setReview] = useState('');

    const onSubmitReview = () => {

    }

    if (!isLoggedIn) {
        return (
            <Container topBorder variant='shadow' className={className} style={{
                ...style
            }}>
                <label htmlFor="reviewInput" className="form-label">Sign in to leave a review!</label>
            </Container>
        )
    }

    return (
        <form>
        <Container topBorder variant='shadow' className={className} style={{
            ...style
        }}>
            <div className="my-3">
                    <label htmlFor="reviewInput" className="form-label py-1">Leave a review!</label>
                    <textarea type="text" className="form-control" id="reviewInput" aria-describedby="reviewHelp"
                        value={review}
                        placeholder="What was your favorite part?"
                        onChange={(e) => {
                            setReview(e.target.value);
                        }}
                        style={{
                            height: '200px',
                            display: 'inline',
                        }}
                    />

                <div className="d-flex my-2">
                    <button type="button" className="btn-login draw meet"
                        onClick={onSubmitReview}
                    >Submit</button>

<ul className="rating" data-mdb-toggle="rating">
  <li>
    <i className="far fa-star fa-sm text-primary" title="Bad"></i>
  </li>
  <li>
    <i className="far fa-star fa-sm text-primary" title="Poor"></i>
  </li>
  <li>
    <i className="far fa-star fa-sm text-primary" title="OK"></i>
  </li>
  <li>
    <i className="far fa-star fa-sm text-primary" title="Good"></i>
  </li>
  <li>
    <i className="far fa-star fa-sm text-primary" title="Excellent"></i>
  </li>
</ul>
                </div>
            </div>
        </Container>
        </form>
    )
}

export default ReviewForm;
