import React, { useEffect, useState } from 'react';
import requests from '../Constants/Requests';

const MoviePage = (props) => {
    const [movie, setMovie] = useState({});
    var tempUrl = props.location.pathname;
    tempUrl = tempUrl.slice(7,tempUrl.length);

    const movieId = tempUrl.slice(0, tempUrl.indexOf('/'));

    useEffect(() => {
        const movieUrl = requests.fetchMovie(movieId).url;
        fetch(movieUrl).then(res => res.json())
        .then(data => {
            if (data.status_message) {
                return;
            }
            setMovie(data);
            console.log(movie);
        });
      }, [])

    const movieNotFound = () => {
        return (
            <h1 className="text-white text-center" style={{
                paddingTop: '10rem'
            }}>Movie not found!</h1>
        )
    }

    const movieInfo = () => {
        return (
            <div className="container-fluid px-0" style={{
            background: 'rgba(29, 29, 35, 1)',
            paddingBottom: '5rem',
            paddingTop: '5rem',
        }}>
            <div className="container-fluid px-4">
                <h1 className="py-2 form-title" style={{
                    fontSize: '48px',
                    textShadow: '4px 4px rgba(80, 80, 100, 1)',
                }}>Login</h1>


                <form className="container-fluid py-3" style={{
                    backgroundColor: 'rgba(80, 80, 100, 1)',
                    borderTop: '4px solid rgba(225, 202, 240, 1)',
                    boxShadow: '10px 10px 20px black',
                    borderRadius: '3px',
                }}>
                <div className="mb-3">
                    <label htmlFor="exampleInputUsername1" className="form-label">Username *</label>
                    <input type="username" className="form-control" id="exampleInputUsername1" aria-describedby="usernameHelp"
                        onChange={(e) => {
                            setUsername(e.target.value);
                        }}
                    />
                </div>

                    <div className="mb-3">
                        <label htmlFor="exampleInputPassword1" className="form-label d-flex">Password *</label>
                        <input type="password" className="form-control" id="exampleInputPassword1"
                            onChange={(e) => {
                                setPassword(e.target.value);
                            }}
                        />
                    </div>

                <button type="button" className="btn-login draw meet"
                    onClick={login}
                >Login</button>
                </form>

                <label className="form-label ms-2 mt-2">Don't have an account? Become a critic <Link to="/register"
                    style={{
                        textDecoration: 'none',
                        color: "rgba(225, 202, 240, 1)",
                        borderBottom: '2px solid rgba(80, 80, 100, 1)'
                    }}
                >here.</Link></label>

            </div> 
        </div>
        )
    }

    return (
        <div className="container-fluid" style={{
            backgroundColor: "rgba(29, 29, 35, 1)",
        }}>
            {movie.hasOwnProperty('title') ?
                movieInfo()
            :
                movieNotFound()
            }
        </div>
    )
}

export default MoviePage;
